func:
// connection Nachtricht bauen
const serialNumber = flow.get("$parent.serialNumber");
const connection = flow.get("$parent.connection");
connection.serialNumber = serialNumber;
connection.timestamp = new Date().toISOString();

msg.topic = flow.get("$parent.MQTT_topic") + "/connection";
msg.payload = connection;
return msg;

msg.topic = flow.get("$parent.MQTT_topic") + "/order";
msg.action = "subscribe";

return msg;

msg.topic = flow.get("$parent.MQTT_topic") + "/instantAction";
msg.action = "subscribe";

return msg;

const state = flow.get("$parent.state");
const loads = flow.get("$parent.loads");
state.timestamp = new Date().toISOString();
state.actionState = null;
state.loads = loads ?? [];
msg.topic = flow.get("$parent.MQTT_topic") + "/state";
msg.payload = state;
return msg;

// Timestamp
const timestamp = new Date().toISOString();

const serialNumberObj = msg.payload.resultsConverted[0] ?? {};
if (!serialNumberObj.value) {
    node.error("SerialNumber is not available", msg.payload);
    return;
}
const serialNumber = serialNumberObj.value.trim().replace(" ", "").replace("-", "");
const modelObj = msg.payload.resultsConverted[1] ?? {};
const model = modelObj.value ?? "Unknown";
const versionObj = msg.payload.resultsConverted[2] ?? {};
const version = versionObj.value ?? "N/A";
msg.model = model;
msg.serialNumber = serialNumber;
msg.version = version

// Werte in Flow speichern
flow.set('$parent.MQTT_topic', 'module/v1/ff/' + serialNumber);
flow.set("$parent.modell", model);
flow.set("$parent.serialNumber", serialNumber);
flow.set("$parent.version", version);

node.status({ shape: "dot", fill: "green", text: `${serialNumber} / ${model}`})

// state serialNumber update
const state = flow.get("$parent.state");
state.serialNumber = serialNumber;
flow.set("$parent.state", state);

// connection update
const connection = flow.get("$parent.connection");
connection.version = version;
flow.set("$parent.connection", connection);

// factsheet Update
const factsheet = flow.get("$parent.factsheet");
factsheet.serialNumber = serialNumber;
factsheet.version = version;
factsheet.typeSpecification.seriesName = "MOD-FF22+" + model + "+24V";
factsheet.typeSpecification.moduleClass = model;
flow.set("$parent.factsheet", factsheet);

return msg;

// state bauen
const state = flow.get("$parent.state");
state.headerId = parseInt(flow.get("$parent.headerId"), 10);
state.loads = flow.get("$parent.loads");
state.actionState = flow.get("$parent.actionState");
state.actionState.id = msg.actionId;
state.actionState.state = "FAILED";
state.actionState.command = msg.order ?? msg.instantAction;
state.timestamp = new Date().toISOString();
state.orderId = msg.orderId;
state.orderUpdateId = msg.orderUpdateId;
const flowErrors = flow.get("$parent.errors");
state.errors = flowErrors ? Array.isArray(flowErrors) ? flowErrors : [flowErrors] : [];

// flow update
flow.set("$parent.headerId", state.headerId + 1);

msg.topic = flow.get("$parent.MQTT_topic") + "/state";
msg.payload = state;
return msg;

if (!msg.instantAction) {
    let error = {
        "errorType": "Validation",
        "errorLevel": "WARNING",
        "errorReferences": [
            { "topic": "order" },
            { "headerId": flow.get("$parent.headerId") },
            { "orderId": msg.orderId },
            { "orderUpdateId": msg.orderUpdateId }
        ],
        "errorMessage": "Not an instantAction"
    }
    // Error in Flow
    flow.set("$parent.errors", error);
    return [undefined, msg];
}
return msg;

const serialNumber = flow.get("$parent.serialNumber") ?? "MISSING-SERIALNUMBER";
if (msg.serialNumber != serialNumber) {
    const headerId = flow.get("$parent.headerId");
    const error = {
        "errorType": "Validation",
        "errorLevel": "WARNING",
        "errorReferences": [
            { "topic": "order" },
            { "headerId": headerId },
            { "orderId": msg.orderId },
            { "orderUpdateId": msg.orderUpdateId }
        ],
        "errorMessage": "SerialNumber does not match"
    }
    // Error in Flow
    flow.set("$parent.errors", error);
    return [null, msg];
}
return msg;

/**
 * @typedef {object} ActionState
 * @property {string} id
 * @property {string} state
 * @property {string} command
 * @property {object} [metadata]
 * 
 * @param {string} state
 * @returns {ActionState}
 */
function updateActionState(state, metadata = undefined) {
    const actionState = flow.get("actionState") ?? {};
    actionState.id = msg.actionId;
    actionState.state = state;
    actionState.command = msg.actionCommand;
    actionState.metadata = metadata ?? undefined;
    flow.set("actionState", actionState);
    return actionState;
}

const actionState = updateActionState("FINISHED");

if (!msg.payload) {
    return;
}

const action = (msg.payload.actions ?? [])[0];
const instantAction = action?.actionType;
const actionId = action?.actionId;
const actionMetadata = action?.metadata ?? {};
const serialNumber = msg.payload.serialNumber ?? "NoSerial";
const orderId = msg.payload.orderId;
const orderUpdateId = msg.payload.orderUpdateId;

msg.instantAction = instantAction;
msg.actionId = actionId;
msg.actionMetadata = actionMetadata;
msg.actionCommand = instantAction;
msg.actionState = updateActionState("PENDING", actionMetadata);
flow.set("$parent.actionState", msg.actionState);
node.status({ fill: "green", shape: "ring", text: msg.instantAction });
msg.serialNumber = serialNumber;
msg.orderId = orderId;
msg.orderUpdateId = orderUpdateId;
return msg;

/**
 * @typedef {object} ModuleAction
 * @property {string} actionType
 * @property {string[]} actionScopes
 * @property {object[]} actionParameters
 * 
 * @typedef {object} ProtocolFeatures
 * @property {ModuleAction[]} moduleActions
 * 
 * @typedef {object} Factsheet
 * @property {ProtocolFeatures} protocolFeatures
 */

/** @type {Factsheet} */
const factsheet = flow.get("$parent.factsheet");
const factsheetInstantActions = factsheet.protocolFeatures.moduleActions.filter(act => {
    return (act.actionScopes ?? []).includes("INSTANT");
}).map(act => act.actionType);
const supportedInstantActions = ["factsheetRequest", "SET_STORAGE", "setStatusLED", "reset", ...factsheetInstantActions];
if (supportedInstantActions.indexOf(msg.instantAction) == -1) {
    return;
}
return msg;

const serialNumber = flow.get("$parent.serialNumber") ?? "MISSING-SERIALNUMBER";
if (msg.serialNumber != serialNumber) {
    const error = {
        "errorType": "Validation",
        "errorLevel": "WARNING",
        "errorReferences": [
            { "topic": "order" },
            { "headerId": flow.get("$parent.headerId") },
            { "orderId": msg.orderId },
            { "orderUpdateId": msg.orderUpdateId }
        ],
        "errorMessage": "SerialNumber does not match"
    }
    // Error in Flow
    flow.set("$parent.errors", error);
    return [null, msg];
}
return msg;

if (!msg.payload) {
    return;
}

const orderCommand = msg.payload.action?.command;
const actionId = msg.payload.action?.id;
const serialNumber = msg.payload.serialNumber ?? "NoSerial";
const orderId = msg.payload.orderId;
const orderUpdateId = msg.payload.orderUpdateId;

msg.order = orderCommand;
msg.orderAction = msg.payload.action;
msg.actionId = actionId;
msg.serialNumber = serialNumber;
msg.orderId = orderId;
msg.orderUpdateId = orderUpdateId;
node.status({ fill: "green", shape: "dot", text: orderCommand });
return msg;

if (!msg.order) {
    let error = {
        "errorType": "Validation",
        "errorLevel": "WARNING",
        "errorReferences": [
            { "topic": "order" },
            { "headerId": flow.get("$parent.headerId") },
            { "orderId": msg.orderId },
            { "orderUpdateId": msg.orderUpdateId }
        ],
        "errorMessage": "Not an order"
    }
    // Error in Flow
    flow.set("$parent.errors", error);
    return [undefined, msg];
}
return msg;

/**
 * @typedef {object} ModuleAction
 * @property {string} actionType
 * @property {string[]} actionScopes
 * @property {object[]} actionParameters
 * 
 * @typedef {object} ProtocolFeatures
 * @property {ModuleAction[]} moduleActions
 * 
 * @typedef {object} Factsheet
 * @property {ProtocolFeatures} protocolFeatures
 */

/** @type {Factsheet} */
const factsheet = flow.get("$parent.factsheet");
const supportedActions = factsheet.protocolFeatures.moduleActions.filter(act => {
    return act.actionScopes == undefined || 
    act.actionScopes.includes("NODE") || 
    act.actionScopes.includes("ORDER");
}).map(act => act.actionType);
if (supportedActions.indexOf(msg.order) == -1) {
    const error = {
        "errorType": "Validation",
        "errorLevel": "WARNING",
        "errorReferences": [
            { "topic": "order" },
            { "headerId": flow.get("$parent.headerId") },
            { "orderId": msg.orderId },
            { "orderUpdateId": msg.orderUpdateId }
        ],
        "errorMessage": `Command "${msg.order}" is not supported`
    }
    // Error in Flow
    flow.set("$parent.errors", error);
    return [undefined, msg];
}
return msg;

const existingOrderId = flow.get("$parent.orderId");
const actionState = flow.get("$parent.actionState.state");

if (actionState == "FINISHED" || actionState == "FAILED") {
    flow.set("$parent.orderId", msg.orderId);
    return msg;
}

if (msg.orderId == existingOrderId || existingOrderId == "0") {
    flow.set("$parent.orderId", msg.orderId);
    return msg;
}

const error = {
    "errorType": "Validation",
    "errorLevel": "WARNING",
    "errorReferences": [
        { "topic": "order" },
        { "headerId": flow.get("$parent.headerId") },
        { "orderId": msg.orderId },
        { "orderUpdateId": msg.orderUpdateId }
    ],
    "errorMessage": `OrderId "${msg.orderId}" not valid`
}
// Error in Flow
flow.set("$parent.errors", error);
return [null, msg];

const knownOrderId = flow.get("$parent.orderId");
const knownOrderUpdateId = flow.get("$parent.orderUpdateId");
const receivedOrderId = msg.orderId;
const receivedOrderUpdateId = msg.orderUpdateId;

if (knownOrderId == receivedOrderId && knownOrderUpdateId == receivedOrderUpdateId) {
    return;
}

return msg;

const factsheet = msg.payload ?? {};

// update the timestamp
factsheet.timestamp = new Date().toISOString();

// set factsheet to payload after all updates
msg.factsheet = factsheet;
msg.payload = factsheet;
return msg;

const globalEnvs = global.get('env');
const variableName = env.get('VARIABLE_NAME');
const endpoint = globalEnvs[variableName];
msg.endpoint = endpoint ?? variableName;
msg.payload = {
    endpoint,
};
return msg;

/**
 * @typedef {object} RackPosition
 * @property {{"#address": string, value: string }} name
 * @property {{"#address": string, value: boolean}} belegt
 * @property {{"#address": string, value: string}} type
 * @property {{"#address": string, value: string}} workpieceId
 */

/** @param {string} nodeId */
function getNodeName(nodeId) {
    /** @type {Record<string, string>} */
    const rack = flow.get("$parent.rack") ?? {};
    flow.set("$parent.rack", rack);
    /** @type {Record<string, string>} */
    const existing = flow.get("$parent.rack.dictionary") ?? {};
    return existing[nodeId] ?? "UNKNOWN";
}

/**
 * @param {string} rackPosition
 * @returns {string|undefined}
 */
function getRackPositionNodeId(rackPosition) {
    /** @type {Record<string, string>} */
    const rack = flow.get("$parent.rack") ?? {};
    flow.set("$parent.rack", rack);
    /** @type {Record<string, string>} */
    const existing = flow.get("$parent.rack.dictionary") ?? {};
    return Object.entries(existing).filter(([_, value]) => value == rackPosition).map(([key]) => key)[0];
}

/** 
 * @param {string} nodeId
 * @param {string} valueName
 * @param {string|boolean} nodeValue 
 */
function writeNodeValue(nodeId, valueName, nodeValue) {
    const rackPos = env.get("RACK_POSITION");
    /** @type {Record<string, string>} */
    const rack = flow.get("$parent.rack") ?? {};
    flow.set("$parent.rack", rack);
    /** @type {RackPosition} */
    const rackPositionNodeId = getRackPositionNodeId(rackPos);
    const storedPosition = flow.get(`$parent.rack.positions.${rackPos}`) ?? {
        name: {
            "#address": rackPositionNodeId,
            value: rackPos
        }
    };
    const strippedValueName = valueName.replace(`rack__${rackPos}_`, "");
    storedPosition[strippedValueName] = {
        "#address": nodeId,
        value: nodeValue
    };
    flow.set(`$parent.rack.positions.${rackPos}`, storedPosition);
    return storedPosition;
}

const rackPos = env.get("RACK_POSITION");
const valueName = getNodeName(msg.payload.nodeId);
/** @type {string|boolean} */
const nodeValue = msg.payload.value.value.value;
const updatedRackPosition = writeNodeValue(msg.payload.nodeId, valueName, nodeValue);
node.status({ fill: "green", shape: "dot", text: `${rackPos}: ${updatedRackPosition.belegt.nodeValue} [${updatedRackPosition.type.nodeValue}]`});
msg.payload = updatedRackPosition;
return msg;

/**
 * @typedef {object} BrowseResult
 * @property {string} nodeId
 * @property {string} browseName
 * @property {string} displayName
 * @property {string} nodeClass
 * @property {string} datatypeName
 */
/** @param {BrowseResult} result */
function extractName({ browseName }) {
  const [_, nodeName] = browseName.split(":");
  return nodeName ?? "UNKNOWN";
}

const browserResults = msg.payload.browserResults ?? [];
/** @type {Record<string, string>} */
const rack = flow.get("$parent.rack") ?? {};
flow.set("$parent.rack", rack);
/** @type {Record<string, string>} */
const existing = flow.get("$parent.rack.dictionary") ?? {};
/** @type {Record<string, string>} */
const dictionary = browserResults.reduce((dict, result) => {
  const nodeName = extractName(result);
  return {
    ...dict,
    [result.nodeId]: nodeName,
    [nodeName]: result.nodeId,
  };
}, existing);
flow.set("$parent.rack.dictionary", dictionary);
return;

/**
 * @typedef {object} RackPosition
 * @property {{value: string}} name
 * @property {{"#address": string, value: string}} workpieceId
 * @property {{"#address": string, value: boolean}} belegt
 * @property {{"#address": string, value: string}} type
 */

/**
 * @typedef {object} Load
 * @property {string} loadType
 * @property {string} loadId
 * @property {string} [loadPosition]
 * 
 * @param {string} rackPosition
 * @param {string} workpieceId
 * @param {string} type
 */
function createLoad(rackPosition, workpieceId, type) {
    return {
        loadType: type,
        loadId: workpieceId,
        loadPosition: rackPosition,
    };
}

function createLoads() {
    /** @type {Record<string, RackPosition>} */
    const rackPositions = flow.get("$parent.rack.positions") ?? [];
    return Object.values(rackPositions).map((pos) => createLoad(pos.name.value, pos.workpieceId.value, pos.type.value));
}

/** @type {RackPosition} */
const rackPosition = msg.payload;
/** @type {Load[]} */
const existing = createLoads();
const updated = existing.map((el) => {
    if (el.loadPosition == rackPosition.name.value) {
        node.status({ shape: "dot", "fill": "green", text: `${el.loadPosition}: ${rackPosition.type.value ?? "EMPTY"} / ${rackPosition.workpieceId.value ?? "-"}` });
        return createLoad(el.loadPosition, rackPosition.workpieceId.value, rackPosition.type.value);
    }
    return el;
});
flow.set("$parent.loads", updated);
flow.set("$parent.state.loads", updated);
return;

/**
 * @typedef {object} ReferenceData
 * @property {string} referenceKey
 * @property {number} referenceValue
 * 
 * @typedef {object} CalibrationData
 * @property {string} infoType
 * @property {string} infoLevel
 * @property {ReferenceData[]} infoReferences
 * 
 * @typedef {Record<string, number>} NodeValues
 * 
 * @typedef {object} CalibrationValue
 * @property {string} nodeId
 * @property {string} nodeName
 * @property {string|NodeValues} nodeValue
 */

/** 
 * @param {string} nodeId 
 * @returns {string} the nodeName
 */
function getNodeName(nodeId) {
    /** @type {Record<string, string>} */
    const calibration = flow.get("$parent.calibration") ?? {};
    flow.set("$parent.calibration", calibration);
    /** @type {Record<string, string>} */
    const existing = flow.get("$parent.calibration.dictionary") ?? {};
    return existing[nodeId] ?? "UNKNOWN";
}

/**
 * @param {string} nodeName
 * @returns {string} the nodeId
 */
function getNodeIdByName(nodeName) {
    /** @type {Record<string, string>} */
    const calibration = flow.get("$parent.calibration") ?? {};
    flow.set("$parent.calibration", calibration);
    /** @type {Record<string, string>} */
    const existing = flow.get("$parent.calibration.dictionary") ?? {};
    return Object.entries(existing).filter(([_, nn]) => nn.toLocaleLowerCase() == nodeName?.toLocaleLowerCase()).map(([nodeId]) => nodeId)[0];
}

/**
 * @param {string} nodeId
 * @param {number} nodeValue
 * @returns {ReferenceData}
 */
function createReferenceData(nodeId, nodeValue) {
    const nodeName = getNodeName(nodeId);
    return {
        referenceKey: nodeName,
        referenceValue: nodeValue,
    };
}

/** 
 * @param {string} nodeId
 * @param {number} nodeValue
 * @returns {CalibrationValue}
 */
function writeNodeValue(nodeId, nodeValue) {
    /** @type {Record<string, CalibrationValue>} */
    const nodeName = getNodeName(nodeId);
    /** @type {CalibrationValue} */
    const calibrationValue = flow.get(`$parent.calibration.state.${nodeName}`) ?? {
        nodeId,
        nodeName,
    };
    calibrationValue.nodeValue = nodeValue;
    flow.set(`$parent.calibration.state.${nodeName}`, calibrationValue);
    return calibrationValue;
}

/**
 * @param {Record<string, number>} nodeValueObject
 * @returns {CalibrationValue[]}
 */
function writeNodeValues(nodeValueObject) {
    // we are only interested in the entries of the nodeValueObject
    return Object.entries(nodeValueObject).map(([nodeName, nodeValue]) => {
        const nodeId = getNodeIdByName(nodeName);
        return writeNodeValue(nodeId, nodeValue);
    });
}

/** @type {string} */
const nodeId = msg.payload.nodeId;
/** @type {number} */
const nodeValue = msg.payload.value.value.value;
const calibrationValue = writeNodeValue(nodeId, nodeValue);
msg.payload = calibrationValue;
msg.topic = "calibration-value__" + nodeId;
return msg;

/**
 * @typedef {object} BrowseResult
 * @property {string} nodeId
 * @property {string} browseName
 * @property {string} displayName
 * @property {string} nodeClass
 * @property {string} datatypeName
 */
/** @param {BrowseResult} result */
function extractName({ browseName }) {
  const [_, nodeName] = browseName.split(":");
  return nodeName ?? "UNKNOWN";
}

const browserResults = msg.payload.browserResults ?? [];

/** @type {Record<string, string>} */
const calibration = flow.get("$parent.calibration") ?? {};
flow.set("$parent.calibration", calibration);

/** @type {Record<string, string>} */
const existing = flow.get("$parent.calibration.dictionary") ?? {};
/** @type {Record<string, string>} */
const dictionary = browserResults.reduce((dict, result) => {
  const nodeName = extractName(result);
  return {
    ...dict,
    [result.nodeId]: nodeName,
    [nodeName]: result.nodeId,
  };
}, existing);
flow.set("$parent.calibration.dictionary", dictionary);
return;

const configuredAvailablePositions = env.get("AVAILABLE_POSITIONS") ?? "";
const configuredCurrentPosition = env.get("CURRENT_POSITION") ?? "";

/** @type {Record<string, string>} */
const calibration = flow.get("$parent.calibration") ?? {};
flow.set("$parent.calibration", calibration);

/** @type {Record<string, string>} */
const positions = flow.get("$parent.calibration.positions") ?? {};
flow.set("$parent.calibration.positions", positions);

flow.set("$parent.calibration.positions.available", configuredAvailablePositions);
flow.set("$parent.calibration.positions.current", configuredCurrentPosition);

return;

/**
 * @typedef {object} Load
 * @property {string} type
 * @property {string} [workpieceId]
 * 
 * @typedef {object} Workpiece
 * @property {string} workpieceId
 * 
 * @typedef {object} Metadata
 * @property {string} [type]
 * @property {string} [workpieceId]
 * @property {Workpiece} [workpiece]
 * 
 * @typedef {object} Action
 * @property {string} id
 * @property {string} command
 * @property {string} [state]
 * @property {any} [result] 
 * @property {Metadata} metadata
 */

/**
 * @param {string} type
 * @param {string} workpieceId
 * @returns {Load}
 */
function createLoad(type, workpieceId) {
    return {
        type,
        workpieceId,
    };
}

/**
 * @param {Action} actionState
 * @param {Load[] | undefined} loads
 */
function createState(actionState, loads = undefined) {
    /** @type {object} */
    const state = flow.get("$parent.state");
    const timestamp = new Date().toISOString();
    const headerId = flow.get("$parent.headerId");

    state.headerId = headerId;
    state.loads = loads ?? state.loads ?? [];
    state.actionState = actionState;
    state.timestamp = timestamp;
    state.orderId = undefined;
    state.orderUpdateId = flow.get("$parent.orderUpdateId");
    state.errors = [];
    state.paused = msg.modulePaused ?? state.paused;
    state.operatingMode = msg.operatingMode ?? state.operatingMode;
    if (Array.isArray(msg.calibrationData)) {
        state.information = msg.calibrationData;
    }

    // headerId Update
    flow.set("$parent.headerId", headerId + 1);
    flow.set("$parent.state", state);
    flow.set("$parent.loads", loads ?? state.loads);
    return state;
}

/**
 * @typedef {object} ActionState
 * @property {string} id
 * @property {string} state
 * @property {string} command
 * @property {object} [metadata]
 * 
 * @param {string} state
 * @returns {ActionState}
 */
function updateActionState(state, metadata = undefined) {
    const actionState = flow.get("$parent.actionState") ?? {};
    actionState.id = msg.actionId ?? actionState.id;
    actionState.state = state;
    actionState.command = msg.actionCommand ?? actionState.command;
    actionState.metadata = metadata ?? actionState.metadata ?? {};
    flow.set("$parent.actionState", actionState);
    return actionState;
}

// state bauen
const actionState = updateActionState("FINISHED", msg.actionMetadata);
const state = createState(actionState, msg.loads);

/** @type {string} */
const parentFlowName = flow.get("$parent.flowName");
node.status({ shape: "dot", fill: "green", text: `${parentFlowName} / ${actionState.command ?? "UNKNOWN"}: ${actionState.state}`});

// flow update
flow.set("$parent.orderId", "0");
flow.set("$parent.orderUpdateId", 0);

msg.topic = flow.get("$parent.MQTT_topic") + "/state";
msg.payload = state;
return msg;

/**
 * @typedef {object} Load
 * @property {string} type
 * @property {string} [workpieceId]
 * 
 * @typedef {object} Workpiece
 * @property {string} workpieceId
 * 
 * @typedef {object} Metadata
 * @property {string} [type]
 * @property {string} [workpieceId]
 * @property {Workpiece} [workpiece]
 * 
 * @typedef {object} Action
 * @property {string} id
 * @property {string} command
 * @property {string} [state]
 * @property {any} [result] 
 * @property {Metadata} metadata
 */

/**
 * @param {string} type
 * @param {string} workpieceId
 * @returns {Load}
 */
function createLoad(type, workpieceId) {
    return {
        type,
        workpieceId,
    };
}

/**
 * @param {Action} actionState
 * @param {Load[] | undefined} loads
 */
function createState(actionState, loads = undefined) {
    /** @type {object} */
    const state = flow.get("$parent.state");
    const timestamp = new Date().toISOString();
    const headerId = flow.get("$parent.headerId");

    state.headerId = headerId;
    state.loads = loads ?? state.loads ?? [];
    state.actionState = actionState;
    state.timestamp = timestamp;
    state.orderId = flow.get("$parent.orderId");
    state.orderUpdateId = flow.get("$parent.orderUpdateId");
    const flowErrors = flow.get("$parent.errors");
    state.errors = flowErrors ? Array.isArray(flowErrors) ? flowErrors : [flowErrors] : [];
    state.paused = msg.modulePaused ?? state.paused;
    state.operatingMode = msg.operatingMode ?? state.operatingMode;

    // headerId Update
    flow.set("$parent.headerId", headerId + 1);
    flow.set("$parent.state", state);
    flow.set("$parent.loads", loads ?? state.loads);
    return state;
}

/**
 * @typedef {object} ActionState
 * @property {string} id
 * @property {string} state
 * @property {string} command
 * @property {object} [metadata]
 * 
 * @param {string} state
 * @returns {ActionState}
 */
function updateActionState(state, metadata = undefined) {
    const actionState = flow.get("$parent.actionState") ?? {};
    actionState.id = msg.actionId ?? actionState.id;
    actionState.state = state;
