if (flow.get("orderId") == msg.payload.orderId || flow.get("orderId") == "0") {
    // orderId im Flow speichern
    flow.set("orderId", msg.payload.orderId);

    // orderUpdateId Fallunterscheidung
    if (msg.payload.orderUpdateId > flow.get("orderUpdateId")) {

        // duration in actionState
        let actionState = flow.get("actionState");
        actionState.command = msg.payload.action.command;
        actionState.id = msg.payload.action.id;
        let loads = flow.get("loads");
        let newLoad = loads[0] ?? {};

        // Command Fallunterscheidung
        if (actionState.command == "PICK" && flow.get("moduleState") == "IDLE") {
            // ModulState busy setzen
            flow.set("moduleState", "PICKBUSY");

            // OPCUA Write Content
            msg.payload.valuesToWrite = [{ "value": true }];
            msg.payload.nodesToWrite = [{ "nodeId": "ns=4;i=4", "datatypeName": "Boolean" }];
        } else if (actionState.command == "DROP" && flow.get("moduleState") == "WAITING_AFTER_MILL") {
            // ModulState busy setzen
            flow.set("moduleState", "DROPBUSY");

            // OPCUA Write Content
            msg.payload.valuesToWrite = [{ "value": true }];
            msg.payload.nodesToWrite = [{ "nodeId": "ns=4;i=5", "datatypeName": "Boolean" }];
        } else if (actionState.command == "DRILL" && flow.get("moduleState") == "WAITING_AFTER_PICK") {
            // state.loads bearbeiten
            newLoad.duration = msg.payload.action.metadata.duration ? msg.payload.action.metadata.duration : 2

            // ModulState busy setzen
            flow.set("moduleState", "MILLBUSY");

            // OPCUA Write Content
            msg.payload.valuesToWrite = [
                { "value": true },
                { "value": newLoad.duration }];
            msg.payload.nodesToWrite = [
                { "nodeId": "ns=4;i=6", "datatypeName": "Boolean" },
                { "nodeId": "ns=4;i=10", "datatypeName": "Int16" }];
            actionState.duration = newLoad.duration;
        } else {
            const flowErrors = flow.get("errors") ?? [];
            const errors = Array.isArray(flowErrors) ? flowErrors : [flowErrors];
            let error = {
                "errorType": "Validation",
                "errorLevel": "WARNING",
                "errorReferences": [
                    { "topic": "order" },
                    { "headerId": flow.get("headerId") },
                    { "orderId": msg.payload.orderId },
                    { "orderUpdateId": msg.payload.orderUpdateId }
                ],
                "errorMessage": "Command not supported"
            }
            // Error in Flow
            flow.set("errors", [...errors, error]);
            return [null, msg];
        }

        // actionState in Flow speichern
        flow.set("actionState", actionState);

        // loads for order im Flow speichern
        newLoad.loadType = msg.payload.action.metadata.type;
        flow.set("loads", [newLoad]);

        // orderUpdateId updaten
        flow.set("orderUpdateId", msg.payload.orderUpdateId);

        // OPCUA write Content
        msg.payload.nodetype = "inject";
        msg.payload.injectType = "write";

        return [msg, null];
    } else {
        const flowErrors = flow.get("errors") ?? [];
        const errors = Array.isArray(flowErrors) ? flowErrors : [flowErrors];
        let error = {
            "errorType": "Validation",
            "errorLevel": "WARNING",
            "errorReferences": [
                { "topic": "order" },
                { "headerId": flow.get("headerId") },
                { "orderId": msg.payload.orderId },
                { "orderUpdateId": msg.payload.orderUpdateId }
            ],
            "errorMessage": "OrderUpdateId not valid"
        }
        // Error in Flow
        flow.set("errors", [...errors, error]);
        return [null, msg];
    }
} else {
    const flowErrors = flow.get("errors") ?? [];
    const errors = Array.isArray(flowErrors) ? flowErrors : [flowErrors];
    let error = {
