 * @property {number | string | undefined} position_dreh
 * @property {number | string | undefined} position_x
 * @property {number | string | undefined} position_y
 */

/**
 * @param {string} message
 * @param {string} [errorLevel]
 */
function createError(message, errorLevel = "WARNING") {
    const flowErrors = flow.get("errors") ?? [];
    const existingErrors = Array.isArray(flowErrors) ? flowErrors : [flowErrors];
    const error = {
        "errorType": "Validation",
        "errorLevel": errorLevel,
        "errorReferences": [
            { "topic": "order" },
            { "headerId": flow.get("headerId") },
            { "orderId": flow.get("orderId") },
            { "orderUpdateId": flow.get("orderUpdateId") }
        ],
        "errorMessage": message
    }
    flow.set("errors", [...existingErrors, error]);
    return error;
}

function createCalibrationData() {
    /** @type {Record<string, CalibrationValue>} */
    const calibrationState = flow.get("calibration.state") ?? {};
    /** @type {Reference[]} */
    const infoReferences = Object.keys(calibrationState)
        .filter(key => key.indexOf("calib__") > -1 && key.indexOf("grenze") == -1)
        .reduce((agg, key) => {
            const { nodeName, nodeValue } = calibrationState[key];
            /** @tye {Reference} */
            const refData = {
                referenceKey: nodeName,
                referenceValue: nodeValue,
            };
            return [
                ...agg,
                refData
            ];
        }, []);
    return {
        infoType: "calibration_data",
        infoLevel: "DEBUG",
        infoReferences,
    };
}

/**
 * @param {string} newCurrentPosition
 * @returns {CalibrationData}
 */
function createCalibrationPositions(newCurrentPosition) {
    const availablePositions = flow.get("calibration.positions.available");
    return {
        infoType: "calibration_status",
        infoLevel: "DEBUG",
        infoReferences: [
            { "referenceKey": "POSITIONS.AVAILABLE", "referenceValue": availablePositions },
            { "referenceKey": "POSITIONS.CURRENT", "referenceValue": newCurrentPosition },
        ]
    }
}

/**
 * @param {ActionMetadata} metadata
 * @returns {{ nodesToWrite: Array, valuesToWrite: Array }}
 */
function createOpcuaUpdate(metadata) {
    if (metadata.position?.length > 0) {
        let nodesToWrite = [];
        let valuesToWrite = [];

        switch (metadata.position) {
            case "TO_NIO_BIN": {
                nodesToWrite = [{ nodeId: "ns=4;i=22", "datatypeName": "Boolean" }];
                valuesToWrite = [{ value: true }];
                break;
            }
            case "TO_CAMERA": {
                nodesToWrite = [{ nodeId: "ns=4;i=22", "datatypeName": "Boolean" }];
                valuesToWrite = [{ value: true }];
                break;
            }
            case "CHECK": {
                nodesToWrite = [{ nodeId: "ns=4;i=22", "datatypeName": "Boolean" }];
                valuesToWrite = [{ value: false }];
                break;
            }
            case "TO_PICKUP": {
                nodesToWrite = [{ nodeId: "ns=4;i=25", "datatypeName": "Boolean" }];
                valuesToWrite = [{ value: true }];
                break;
            }
        }
        return { nodesToWrite, valuesToWrite };
    }
    return { nodesToWrite: [], valuesToWrite: [] };
}

