 */
function createOpcuaUpdate() {
    const currentPosition = flow.get("calibration.positions.current");
    if (currentPosition?.length > 0) {
        let nodesToWrite = [];
        let valuesToWrite = [];

        switch (currentPosition) {
            case "TO_NIO_BIN": {
                const nodeValue = flow.get("calibration.state.calib__zeitAusschuss.nodeValue") ?? 0;
                nodesToWrite = [{ nodeId: "ns=4;i=23", "datatypeName": "Int32" }];
                valuesToWrite = [{ value: nodeValue }];
                break;
            }
            case "TO_CAMERA": {
                const nodeValue = flow.get("calibration.state.calib__zeitKamera.nodeValue") ?? 0;
                nodesToWrite = [{ nodeId: "ns=4;i=23", "datatypeName": "Int32" }];
                valuesToWrite = [{ value: nodeValue }];
                break;
            }
            case "CHECK": {
                const nodeValue = 0;
                nodesToWrite = [{ nodeId: "ns=4;i=23", "datatypeName": "Int32" }];
                valuesToWrite = [{ value: nodeValue }];
                break;
            }
            case "TO_PICKUP": {
                // We stop the movement, if the workpiece is detected by the photoelectric sensor
                const nodeValue = 60000;
                nodesToWrite = [{ nodeId: "ns=4;i=23", "datatypeName": "Int32" }];
                valuesToWrite = [{ value: nodeValue }];
                break;
            }
        }
        return { nodesToWrite, valuesToWrite };
    }
    return { nodesToWrite: [], valuesToWrite: [] };
}

