/**
 * @param {ActionMetadata} metadata
 * @returns {{ nodesToWrite: Array, valuesToWrite: Array }}
 */
function createOpcuaUpdate(metadata) {
    if (metadata.position?.length > 0) {
        let nodesToWrite = [];
        let valuesToWrite = [];

        switch (metadata.position) {
            case "TO_NIO_BIN": {
                nodesToWrite = [{ nodeId: "ns=4;i=22", "datatypeName": "Boolean" }];
                valuesToWrite = [{ value: true }];
                break;
            }
            case "TO_CAMERA": {
                nodesToWrite = [{ nodeId: "ns=4;i=22", "datatypeName": "Boolean" }];
                valuesToWrite = [{ value: true }];
                break;
            }
            case "CHECK": {
                nodesToWrite = [{ nodeId: "ns=4;i=22", "datatypeName": "Boolean" }];
                valuesToWrite = [{ value: false }];
                break;
            }
            case "TO_PICKUP": {
                nodesToWrite = [{ nodeId: "ns=4;i=25", "datatypeName": "Boolean" }];
                valuesToWrite = [{ value: true }];
                break;
            }
        }
        return { nodesToWrite, valuesToWrite };
    }
    return { nodesToWrite: [], valuesToWrite: [] };
}

