    const metadata = msg.actionMetadata;
    const { nodesToWrite, valuesToWrite } = createOpcuaUpdate(metadata);
    msg.calibrationData = [createCalibrationData(), createCalibrationPositions(metadata)];
    msg.actionCommand = "testCalibrationPosition";
    msg.payload = {
        nodetype: 'inject',
        injectType: 'write',
        nodesToWrite,
        valuesToWrite
    };
    return msg;
} else {
    createError("Module is not in CALIBRATION mode");
    return [null, msg];
}

