/**
 * @param {string} nodeName
 * @param {number} nodeValue
 * @returns {ReferenceData}
 */
function createReferenceData(nodeName, nodeValue) {
    return {
        referenceKey: nodeName,
        referenceValue: nodeValue,
    };
}

/**
 * @returns {CalibrationData}
 */
function createCalibrationData() {
    /** @type {Record<string, CalibrationValue>} */
    const calibrationState = flow.get("calibration.state") ?? {};
    /** @type {Reference[]} */
    const infoReferences = Object.keys(calibrationState)
        .filter(key => key.indexOf("calib__") > -1 && key.indexOf("grenze") == -1)
        .reduce((agg, key) => {
            const { nodeName, nodeValue } = calibrationState[key];
            /** @tye {Reference} */
            const refData = {
                referenceKey: nodeName,
                referenceValue: nodeValue,
            };
            return [
                ...agg,
                refData
            ];
        }, []);
    return {
        infoType: "calibration_data",
        infoLevel: "DEBUG",
        infoReferences,
    };
}

/**
 * @returns {CalibrationData}
 */
function createCalibrationPositions() {
    const availablePositions = flow.get("calibration.positions.available");
    const currentPosition = flow.get("calibration.positions.current");
    return {
        infoType: "calibration_status",
        infoLevel: "DEBUG",
        infoReferences: [
            { "referenceKey": "POSITIONS.AVAILABLE", "referenceValue": availablePositions },
            { "referenceKey": "POSITIONS.CURRENT", "referenceValue": currentPosition },
        ]
    }
}

