 * @property {string} [position]
 * @property {boolean} [factory]
 * @property {{referenceKey: string, referenceValue: number}[]} [references]
 * 
 * @typedef {object} Action
 * @property {"startCalibration" 
 *    | "stopCalibration" 
 *    | "setCalibrationValues" 
 *    | "storeCalibrationValues" 
 *    | "selectCalibrationPosition" 
 *    | "testCalibrationPosition" 
 *    | "resetCalibration"} actionType
 * @property {string} actionId
 * @property {ActionMetadata} metadata
 * 
 * @typedef {object} CalibrationInstantAction
 * @property {string} serialNumber
 * @property {Date} timestamp
 * @property {Action[]} actions
 * 
 * @typedef {object} ReferenceData
 * @property {string} referenceKey
 * @property {number} referenceValue
 * 
 * @typedef {object} CalibrationData
 * @property {string} infoType
 * @property {string} infoLevel
 * @property {ReferenceData[]} infoReferences
 * 
 * @typedef {object} CalibrationValue
 * @property {string} nodeId
 * @property {string} nodeName
 * @property {string} nodeValue
 */

const moduleState = flow.get("moduleState");
if (moduleState == "CALIBRATION") {
    msg.actionCommand = "resetCalibration";
    msg.payload = {
        nodetype: 'inject',
        injectType: 'write',
        nodesToWrite: [
            { nodeId: "ns=4;i=20", "datatypeName": "Boolean" }
        ],
        valuesToWrite: [
            { value: true }
        ]
    };
    return msg;
} else {
    const error = {
        "errorType": "Validation",
        "errorLevel": "WARNING",
        "errorReferences": [
            { "topic": "order" },
            { "headerId": flow.get("headerId") },
            { "orderId": flow.get("orderId") },
            { "orderUpdateId": flow.get("orderUpdateId") }
        ],
        "errorMessage": "Module is not in CALIBRATION mode"
    }
    flow.set("errors", error);
    return [null, msg];
}

let orderAction = msg.orderAction;

// get current actionState 
let actionState = {};
actionState.command = orderAction.command;
actionState.id = orderAction.id;
actionState.metadata = orderAction.metadata;

// actionState in Flow speichern
flow.set("actionState", actionState);
return msg;

/**
 * @typedef {object} Reference
 * @property {string} referenceKey
 * @property {number} referenceValue
 * 
 * @typedef {object} ActionMetadata
 * @property {string} [position]
 * @property {boolean} [factory]
 * @property {Reference[]} [references]
 * 
 * @typedef {object} Action
 * @property {"startCalibration" 
 *    | "stopCalibration" 
 *    | "setCalibrationValues" 
 *    | "storeCalibrationValues" 
 *    | "selectCalibrationPosition" 
 *    | "testCalibrationPosition" 
 *    | "resetCalibration"} actionType
 * @property {string} actionId
 * @property {ActionMetadata} metadata
 * 
 * @typedef {object} CalibrationInstantAction
 * @property {string} serialNumber
 * @property {Date} timestamp
 * @property {Action[]} actions
 * 
 * @typedef {object} ReferenceData
 * @property {string} referenceKey
 * @property {number} referenceValue
 * 
 * @typedef {object} CalibrationData
 * @property {string} infoType
 * @property {string} infoLevel
 * @property {ReferenceData[]} infoReferences
 * 
 * @typedef {object} CalibrationValue
 * @property {string} nodeId
 * @property {string} nodeName
 * @property {string} nodeValue
 *
 * @typedef {object} PositionTargetData
 * @property {number | string | undefined} position_dreh
 * @property {number | string | undefined} position_x
 * @property {number | string | undefined} position_y
 */

/**
 * @param {string} nodeName
 * @param {number} nodeValue
 * @returns {ReferenceData}
 */
function createReferenceData(nodeName, nodeValue) {
    return {
        referenceKey: nodeName,
        referenceValue: nodeValue,
    };
}

/**
 * @returns {CalibrationData}
 */
function createCalibrationData() {
    /** @type {Record<string, CalibrationValue>} */
    const calibrationState = flow.get("calibration.state") ?? {};
    /** @type {Reference[]} */
    const infoReferences = Object.keys(calibrationState)
        .filter(key => key.indexOf("calib__") > -1 && key.indexOf("grenze") == -1)
        .reduce((agg, key) => {
            const { nodeName, nodeValue } = calibrationState[key];
            /** @tye {Reference} */
            const refData = {
                referenceKey: nodeName,
                referenceValue: nodeValue,
            };
            return [
                ...agg,
                refData
            ];
        }, []);
    return {
        infoType: "calibration_data",
        infoLevel: "DEBUG",
        infoReferences,
    };
}

/**
 * @returns {CalibrationData}
 */
function createCalibrationPositions() {
    const availablePositions = flow.get("calibration.positions.available");
    const currentPosition = flow.get("calibration.positions.current");
    return {
        infoType: "calibration_status",
        infoLevel: "DEBUG",
        infoReferences: [
            { "referenceKey": "POSITIONS.AVAILABLE", "referenceValue": availablePositions },
            { "referenceKey": "POSITIONS.CURRENT", "referenceValue": currentPosition },
        ]
    }
}

msg.calibrationData = [createCalibrationData(), createCalibrationPositions()];
return msg;

let moduleState = flow.get("moduleState");
let value = msg.payload.value.value.value;

if (value != true) {
    return;
} else {
    if (moduleState != "RCQ") {
        msg.payload = {
            "value": value,
            "moduleState": moduleState
        };
        return msg;
    } else {
        // modulestate auf waiting for Result
        flow.set("moduleState", "WR");
        let actionState = flow.get("actionState");
        let action = {};
        action.command = actionState.command;
        action.id = actionState.id;
        action.metadata = actionState.metadata;

        // Timestamp
        let now = new Date();
        let timestamp = now.toISOString();

        // Nachricht an den Controller mit Kamera
        msg.payload = {
            "serialNumber": flow.get("serialNumber"),
            "orderId": flow.get("orderId"),
            "orderUpdateId": flow.get("orderUpdateId" ) + 10,
            "timestamp": timestamp,
            "action": action
        }

        msg.topic = flow.get("TXT_MQTT_topic") + "/order";

        return msg;
    }
}

