const nodeValue = msg.payload.value.value.value;
const moduleStsate = flow.get("moduleState");
if (nodeValue == true && moduleStsate == "RPICK") {
    const state = flow.get("state");
    const timestamp = new Date().toISOString();
    const headerId = flow.get("headerId");
    const actionState = flow.get("actionState");
    actionState.state = "FINISHED"; // Waiting for FTS

    state.headerId = headerId;
    state.loads = flow.get("loads");
    state.actionState = flow.get("actionState");
    state.timestamp = timestamp;
    state.orderId = flow.get("orderId");
    state.orderUpdateId = flow.get("orderUpdateId");
    const flowErrors = flow.get("errors") ?? [];
    state.errors = Array.isArray(flowErrors) ? flowErrors : [flowErrors];

    // headerId Update
    flow.set("headerId", headerId + 1);
    flow.set("orderId", 0);
    flow.set("orderUpdateId", 0);
    flow.set("moduleState", "IDLE");

    msg.topic = flow.get("MQTT_topic") + "/state";
    msg.payload = state;
    return msg;
} else {
    return;
}

const nodeValue = msg.payload.value.value.value;
const moduleState = flow.get("moduleState");
if (nodeValue == true && moduleState == "RDROP") {
    const state = flow.get("state");
    const timestamp = new Date().toISOString();
    const headerId = flow.get("headerId");
    const actionState = flow.get("actionState");
    actionState.state = "FINISHED";
    // get the workpieceId from the flow state (set during initial determination of the rack position)
    let workpieceId = flow.get("workpieceId");
    // unknown should never happen if a workpiece is introduced via DPS
    actionState.result = workpieceId ?? "unknown"; 

    // state
    state.headerId = headerId;
    state.loads = flow.get("loads");
    state.actionState = actionState;
    state.timestamp = timestamp;
    state.orderId = flow.get("orderId");
    state.orderUpdateId = flow.get("orderUpdateId");
    const flowErrors = flow.get("errors") ?? [];
    state.errors = Array.isArray(flowErrors) ? flowErrors : [flowErrors];

    // headerId Update
    flow.set("headerId", headerId + 1);
    flow.set("orderId", 0);
    flow.set("orderUpdateId", 0);
    flow.set("moduleState", "IDLE");
    flow.set("workpieceId", undefined);

    msg.topic = flow.get("MQTT_topic") + "/state";
    msg.payload = state;
    return msg;
} else {
    return;
}

/**
 * @typedef {object} ActionState
 * @property {string} id
 * @property {string} state
 * @property {string} command
 * @property {object} [metadata]
 * 
 * @param {string} state
 * @returns {ActionState}
 */
function updateActionState(state, metadata = undefined) {
    const actionState = flow.get("actionState") ?? {};
    actionState.id = msg.actionId;
    actionState.state = state;
    actionState.command = msg.actionCommand;
    actionState.metadata = metadata ?? undefined;
    flow.set("actionState", actionState);
    return actionState;
}

