    const isCalibActive = flow.get("calibration.state.status__kalibAktiv.nodeValue") ?? false;
    msg.calibrationData = [createCalibrationData(), createCalibrationPositions()];
    msg.modulePaused = true;
    msg.operatingMode = "TEACHIN";
    msg.actionCommand = "startCalibration";
    msg.payload = {
        nodetype: 'inject',
        injectType: 'write',
        nodesToWrite: [
            { nodeId: "ns=4;i=251", "datatypeName": "Boolean" }
        ],
        valuesToWrite: [
            { value: !isCalibActive }
        ]
    };
    return msg;
