 */

/**
 * @param {string} message
 */
function createError(message) {
    const error = {
        "errorType": "Validation",
        "errorLevel": "WARNING",
        "errorReferences": [
            { "topic": "order" },
            { "headerId": flow.get("headerId") },
            { "orderId": flow.get("orderId") },
            { "orderUpdateId": flow.get("orderUpdateId") }
        ],
        "errorMessage": message
    }
    flow.set("errors", error);
    return error;
