/**
 * @param {ActionMetadata} metadata
 * @returns {{ nodesToWrite: Array, valuesToWrite: Array }}
 */
function createOpcuaUpdate(metadata) {
    if (metadata.references?.length > 0) {
        const nodesToWrite = [];
        const valuesToWrite = [];
        for (let ref of metadata.references) {
            /** @type {CalibrationValue} */
            const toUpdate = flow.get(`calibration.state.${ref.referenceKey}`);
            nodesToWrite.push({ nodeId: toUpdate.nodeId, "datatypeName": "Int32" });
            valuesToWrite.push({ value: ref.referenceValue });
        }
        return { nodesToWrite, valuesToWrite };
    }
    return { nodesToWrite: [], valuesToWrite: [] };
}

