 * @returns {{ nodesToWrite: Array, valuesToWrite: Array }}
 */
function createOpcuaUpdate(metadata) {
    if (metadata.references?.length > 0) {
        const nodesToWrite = [];
        const valuesToWrite = [];
        for (let ref of metadata.references) {
            /** @type {CalibrationValue} */
            const toUpdate = flow.get(`calibration.state.${ref.referenceKey}`);
            nodesToWrite.push({ nodeId: toUpdate.nodeId, "datatypeName": "Int32" });
            valuesToWrite.push({ value: toUpdate.nodeValue });
        }
        return { nodesToWrite, valuesToWrite };
    }
    return { nodesToWrite: [], valuesToWrite: [] };
}

/**
 * @param {string} nodeName
 * @param {number} nodeValue
 * @returns {ReferenceData}
 */
function createReferenceData(nodeName, nodeValue) {
    return {
        referenceKey: nodeName,
        referenceValue: nodeValue,
    };
}

/**
 * @returns {CalibrationData}
 */
function createCalibrationData() {
    /** @type {Record<string, CalibrationValue>} */
    const calibrationState = flow.get("calibration.state") ?? {};
    /** @type {Reference[]} */
    const infoReferences = Object.keys(calibrationState)
        .filter(key => key.indexOf("kalib__") > -1 && key.indexOf("grenze") == -1)
        .reduce((agg, key) => {
            const { nodeName, nodeValue } = calibrationState[key];
            /** @tye {Reference} */
            const refData = {
                referenceKey: nodeName,
                referenceValue: nodeValue,
            };
            return [
                ...agg,
                refData
            ];
        }, []);
    return {
        infoType: "calibration_data",
        infoLevel: "DEBUG",
        infoReferences,
