/**
 * @param {ActionMetadata} metadata
 * @returns {{ nodesToWrite: Array, valuesToWrite: Array }}
 */
function createOpcuaUpdate(metadata) {
    if (metadata.position?.length > 0) {
        /** @type {Reference[]} */
        const targetRefs = [];
        const nodesToWrite = [];
        const valuesToWrite = [];
        /** @type {PositionTargetData} */
        const targetData = positions[metadata.position];
        for (let [key, value] of Object.entries(targetData)) {
            /** @type {Reference | null} */
            let newRef = null;
            if (value == undefined) {
                continue;
            } else if (typeof value == "string") {
                const nodeValue = flow.get(`calibration.state.${value}.nodeValue`);
                /** @type {Reference} */
                newRef = {
                    referenceKey: key,
                    referenceValue: nodeValue,
                };
            } else {
                /** @type {Reference} */
                newRef = {
                    referenceKey: key,
                    referenceValue: value,
                };
            }
            targetRefs.push(newRef);
        }

        for (let { referenceKey, referenceValue } of targetRefs) {
            /** @type {CalibrationValue} */
            const toUpdate = flow.get(`calibration.state.${referenceKey}`);
            nodesToWrite.push({ nodeId: toUpdate.nodeId, "datatypeName": "Int32" });
            valuesToWrite.push({ value: referenceValue });
        }
        return { nodesToWrite, valuesToWrite };
    }
    return { nodesToWrite: [], valuesToWrite: [] };
}

/**
 * @returns {{ nodesToWrite: Array, valuesToWrite: Array }}
 */
function createOpcuaPark() {
    return {
        nodesToWrite: [
            { nodeId: "ns=4;i=316", "datatypeName": "Boolean" }
        ],
        valuesToWrite: [
            { value: true }
        ]
    };
}

const moduleState = flow.get("moduleState");
if (moduleState == "CALIBRATION") {
    /** @type {ActionMetadata} */
    const metadata = msg.actionMetadata;
    /** @type {string[]} */
    const availablePosition = flow.get("calibration.positions.available");
    if (availablePosition.indexOf(metadata.position) == -1) {
        createError(`${metadata.position} is not a valid position`);
        return [null, msg];
    }

    const newPosition = metadata.position;
    flow.set("calibration.positions.current", newPosition);

    const newData = (newPosition == "PARK") ? createOpcuaPark() : createOpcuaUpdate(metadata);
    const { nodesToWrite, valuesToWrite } = newData;

    msg.calibrationData = [createCalibrationData(), createCalibrationPositions(newPosition)];
    msg.actionCommand = "selectCalibrationPosition";
    msg.payload = {
        nodetype: 'inject',
        injectType: 'write',
        nodesToWrite,
        valuesToWrite,
    };
    return msg;
} else {
    const error = {
        "errorType": "Validation",
        "errorLevel": "WARNING",
        "errorReferences": [
            { "topic": "order" },
            { "headerId": flow.get("headerId") },
            { "orderId": flow.get("orderId") },
            { "orderUpdateId": flow.get("orderUpdateId") }
        ],
        "errorMessage": "Module is not in CALIBRATION mode"
    }
    flow.set("errors", error);
    return [null, msg];
}

