/**
 * @typedef {object} RackPosition
 * @property {{"#address": string, value: string }} name
 * @property {{"#address": string, value: boolean}} belegt
 * @property {{"#address": string, value: string}} type
 * @property {{"#address": string, value: string}} workpieceId
 */

function getEmptyRackPositions() {
    /** @type Record<string, RackPosition> */
    const rackPositions = flow.get("rack.positions") ?? {};
    return Object.keys(rackPositions)
        .sort((a, b) => {
            const letterA = a.charAt(0);
            const letterB = b.charAt(0);
            const numberA = parseInt(a.substring(1));
            const numberB = parseInt(b.substring(1));

            if (numberA === numberB) {
                return letterA.localeCompare(letterB);
            } else {
                return numberA - numberB;
            }
        })
        .map(k => rackPositions[k])
        .filter(r => r.belegt?.value == false);
}

/**
 * @param {string} type
 * @param {string} workpieceId
 * @return {{valuesToWrite: Array<object>, nodesToWrite: Array<object>}}
 */
function createPickCommand(type, workpieceId) {
    const emptyRackPositions = getEmptyRackPositions();
    const emptyRackPosition = emptyRackPositions[0];
    node.status({ fill: "grey", shape: "dot", text: emptyRackPosition?.name?.value ?? "UNKNOWN" });
    if (!emptyRackPosition) {
        throw new Error("No empty rack");
    }
    return {
        nodesToWrite: [
            { nodeId: 'ns=4;i=245' /* PICK-RACK */, "datatypeName": "String" },
            { nodeId: 'ns=4;i=244'/* PICK */, "datatypeName": "Boolean" },
            { nodeId: emptyRackPosition.belegt['#address'], "datatypeName": "Boolean" },
            { nodeId: emptyRackPosition.type['#address'], "datatypeName": "String" },
            { nodeId: emptyRackPosition.workpieceId['#address'], "datatypeName": "String" }
        ],
        valuesToWrite: [
            { value: emptyRackPosition.name.value },
            { value: true },
            { value: true },
            { value: type },
            { value: workpieceId }
        ]
    };
}

/**
 * @typedef {object} OrderPayload
 * @property {string} timestamp
 * @property {string} orderId
 * @property {number} orderUpdateId
 * @property {string} serialNumber
 * @property {{id: string, command: string, metadata: {type: string, workpieceId: string}}} action 
 * 
 * @param {OrderPayload} payload
 * @return {[type: string, workpieceId: string]}
 */
function extractPickParams(payload) {
    const actionMetadata = payload.action.metadata;
    return [actionMetadata.type, actionMetadata.workpieceId];
}

// wenn nicht IDLE dann nichts tun
const currentModuleState = flow.get("moduleState") ?? "UNKNOWN";
node.status({ fill: "green", shape: "dot", text: currentModuleState });
if (currentModuleState == "IDLE") {
    // ModulState busy setzen
    flow.set("moduleState", "RPICK"); // modulestate zu "RUNNING PICK" setzen
    node.status({ fill: "grey", shape: "dot", text: "RPICK" });

    // Retrieve current Storage information and pick the next 
    try {
        const [type, workpieceId] = extractPickParams(msg.payload);
        node.status({ fill: "green", shape: "dot", text: `${type} / ${workpieceId}` });
        const opcuaCommand = createPickCommand(type, workpieceId);
        node.warn({ opcuaCommand });
        msg.payload = {
            "nodetype": "inject",
            "injectType": "write",
            ...opcuaCommand
        };
        return [msg, null];
    } catch (err) {
        flow.set("moduleState", "IDLE");
        createError(err.message);
        return [null, msg];
    }
} else {
    createError("Module is not IDLE");
    node.status({ fill: "red", shape: "dot", text: "Not IDLE" });
    return [null, msg];
}

