/** @param {string} type */
function getRackPositionByType(type) {
    /** @type Record<string, RackPosition> */
    const rackPositions = flow.get("rack.positions") ?? {};
    return Object.keys(rackPositions)
        .sort((a, b) => {
            const letterA = a.charAt(0);
            const letterB = b.charAt(0);
            const numberA = parseInt(a.substring(1));
            const numberB = parseInt(b.substring(1));

            if (numberA === numberB) {
                return letterA.localeCompare(letterB);
            } else {
                return numberA - numberB;
            }
        })
        .map(k => rackPositions[k])
        .filter(r => {
            node.warn({ r });
            return r.type?.value == type;
        });
}

/**
 * @param {string} type
 * @return {{valuesToWrite: Array<object>, nodesToWrite: Array<object>}}
 */
function createDropCommand(type) {
    node.status({ fill: 'green', shape: 'dot', text: `${type}` });
    const rackPositionsForType = getRackPositionByType(type);
    node.warn({ rackPositionsForType });
    const firstRackPositionForType = rackPositionsForType[0];
    node.warn({ firstRackPositionForType });
    node.status({ fill: 'gray', shape: 'dot', text: firstRackPositionForType?.name?.value ?? "N/A" });
    if (!firstRackPositionForType) {
        throw new Error("Type not available");
    }
    // set the retrieved workpiece's ID to the flow state to access it when finishing the drop command 
    flow.set("workpieceId", firstRackPositionForType?.workpieceId?.value);
    return {
        nodesToWrite: [
            { nodeId: 'ns=4;i=248' /* DROP-RACK */, "datatypeName": "String" },
            { nodeId: 'ns=4;i=249'/* DROP */, "datatypeName": "Boolean" },
            { nodeId: firstRackPositionForType.belegt['#address'], "datatypeName": "Boolean" },
            { nodeId: firstRackPositionForType.type['#address'], "datatypeName": "String" },
            { nodeId: firstRackPositionForType.workpieceId['#address'], "datatypeName": "String" }
        ],
        valuesToWrite: [
            { value: firstRackPositionForType.name.value },
            { value: true },
            { value: false },
            { value: "" },
            { value: "" }
        ]
    };
}

