        const type = extractDropParams(msg.payload);
        const opcuaCommand = createDropCommand(type);
        msg.payload = {
            "nodetype": "inject",
            "injectType": "write",
            ...opcuaCommand
        };
        return [msg, null];
    } catch (err) {
        flow.set("moduleState", "IDLE");
        createError(err.message);
        return [null, msg];
    }
