/**
 * @param {ActionMetadata} metadata
 * @returns {{ nodesToWrite: Array, valuesToWrite: Array }}
 */
function createOpcuaUpdate(metadata) {
    if (metadata.position?.length > 0) {
        /** @type {Reference[]} */
        const targetRefs = [];
        const nodesToWrite = [];
        const valuesToWrite = [];
        /** @type {PositionTargetData} */
        const targetData = positions[metadata.position];
        for (let [key, value] of Object.entries(targetData)) {
            /** @type {Reference | null} */
            let newRef = null;
            if (value == undefined) {
                continue;
            } else if (typeof value == "string") {
                const nodeValue = flow.get(`calibration.state.${value}.nodeValue`);
                /** @type {Reference} */
                newRef = {
                    referenceKey: key,
                    referenceValue: nodeValue,
                };
            } else {
                /** @type {Reference} */
                newRef = {
                    referenceKey: key,
                    referenceValue: value,
                };
            }
            targetRefs.push(newRef);
        }

        for (let { referenceKey, referenceValue } of targetRefs) {
            /** @type {CalibrationValue} */
            const toUpdate = flow.get(`calibration.state.${referenceKey}`);
            nodesToWrite.push({ nodeId: toUpdate.nodeId, "datatypeName": "Int32" });
            valuesToWrite.push({ value: referenceValue });
        }
        return { nodesToWrite, valuesToWrite };
    }
    return { nodesToWrite: [], valuesToWrite: [] };
}

