/**
 * @typedef {object} Load
 * @property {string} loadType
 * @property {string} loadId
 * @property {string} [loadPosition]
 * 
 * @param {string} rackPosition
 * @param {string} workpieceId
 * @param {string} type
 */
function createLoad(rackPosition, workpieceId, type) {
    return {
        loadType: type,
        loadId: workpieceId,
        loadPosition: rackPosition,
    };
}

/**
 * @param {Record<string, { type: string, workpieceId: string}>} contents
 * @returns {Load[]}
 */
function fillLoads(contents) {
    return Object.entries(contents).map(([key, val]) => createLoad(key, val.workpieceId ?? "", val.type ?? ""));
}

/**
 * @typedef {object} OpcuaCommand
 * @property {array} nodesToWrite
 * @property {array} valuesToWrite
 * 
 * @param {Load[]} loads
 * @returns {OpcuaCommand}
 */
function createOpcuaCommand(loads) {
    const rackPositions = flow.get("rack.positions") ?? {};
    const nodesToWrite = [];
    const valuesToWrite = [];

    for (let [rackPos, entry] of Object.entries(rackPositions)) {
        const targetLoad = loads.find(l => l.loadPosition == rackPos);
        nodesToWrite.push({ nodeId: entry.workpieceId['#address'], datatypeName: "String" });
        valuesToWrite.push({ value: targetLoad?.loadId ?? "" });
        nodesToWrite.push({ nodeId: entry.type['#address'], datatypeName: "String" });
        valuesToWrite.push({ value: targetLoad?.loadType ?? "" });
        nodesToWrite.push({ nodeId: entry.belegt['#address'], datatypeName: "Boolean" });
        valuesToWrite.push({ value: !!targetLoad && !!targetLoad?.loadType });
    }

    return { nodesToWrite, valuesToWrite };
}

