 * @returns {{ nodesToWrite: Array, valuesToWrite: Array }}
 */
function createOpcuaPark() {
    return {
        nodesToWrite: [
            { nodeId: "ns=4;i=316", "datatypeName": "Boolean" }
        ],
        valuesToWrite: [
            { value: true }
        ]
    };
}

const moduleState = flow.get("moduleState");
if (moduleState == "CALIBRATION") {
    /** @type {ActionMetadata} */
    const metadata = msg.actionMetadata;
    /** @type {string[]} */
    const availablePosition = flow.get("calibration.positions.available");
    if (availablePosition.indexOf(metadata.position) == -1) {
        createError(`${metadata.position} is not a valid position`);
        return [null, msg];
    }

    const newPosition = metadata.position;
    flow.set("calibration.positions.current", newPosition);

    const newData = (newPosition == "PARK") ? createOpcuaPark() : createOpcuaUpdate(metadata);
    const { nodesToWrite, valuesToWrite } = newData;

    msg.calibrationData = [createCalibrationData(), createCalibrationPositions(newPosition)];
    msg.actionCommand = "selectCalibrationPosition";
    msg.payload = {
        nodetype: 'inject',
        injectType: 'write',
        nodesToWrite,
        valuesToWrite,
    };
    return msg;
} else {
