        infoType: "calibration_status",
        infoLevel: "DEBUG",
        infoReferences: [
            { "referenceKey": "POSITIONS.AVAILABLE", "referenceValue": availablePositions },
            { "referenceKey": "POSITIONS.CURRENT", "referenceValue": newCurrentPosition },
        ]
    }
}

/**
 * @param {ActionMetadata} metadata
 * @returns {{ nodesToWrite: Array, valuesToWrite: Array }}
 */
function createOpcuaUpdate(metadata) {
    if (metadata.position?.length > 0) {
        const nodesToWrite = [];
        const valuesToWrite = [];

        switch (metadata.position) {
            case "CAMERA_HOME": {
                const posX = flow.get("calibration.state.kalib__position_home_X.nodeValue") ?? 0;
                const posY = flow.get("calibration.state.kalib__position_home_Y.nodeValue") ?? 0;
                nodesToWrite.push({ nodeId: "ns=4;i=39", "datatypeName": "Int32" })
                valuesToWrite.push({ value: posX });
                nodesToWrite.push({ nodeId: "ns=4;i=40", "datatypeName": "Int32" })
                valuesToWrite.push({ value: posY });
                break;
            }
            case "CAMERA_HBW": {
                const posX = flow.get("calibration.state.kalib__position_hbw_X.nodeValue") ?? 0;
                const posY = flow.get("calibration.state.kalib__position_hbw_Y.nodeValue") ?? 0;
                nodesToWrite.push({ nodeId: "ns=4;i=39", "datatypeName": "Int32" })
                valuesToWrite.push({ value: posX });
                nodesToWrite.push({ nodeId: "ns=4;i=40", "datatypeName": "Int32" })
                valuesToWrite.push({ value: posY });
                break;
            }
            case "PARK": {
                nodesToWrite.push({ nodeId: "ns=4;i=56", "datatypeName": "Boolean" });
                valuesToWrite.push({ value: true });
                break;
            }
        }
        return { nodesToWrite, valuesToWrite };
    }
    return { nodesToWrite: [], valuesToWrite: [] };
}

const moduleState = flow.get("moduleState");
if (moduleState == "CALIBRATION") {
    /** @type {ActionMetadata} */
    const metadata = msg.actionMetadata;
    /** @type {string[]} */
    const availablePosition = flow.get("calibration.positions.available");
    if (availablePosition.indexOf(metadata.position) == -1) {
        return [null, msg, null];
    }
    // The park position needs to be set for both OPCUA and TXT4.0
    const forwardMsg = (metadata.position == "PARK") ? RED.util.cloneMessage(msg) : null;

    const newPosition = metadata.position;
    flow.set("calibration.positions.current", newPosition);

    const { nodesToWrite, valuesToWrite } = createOpcuaUpdate(metadata);
    msg.payload = {
        nodetype: 'inject',
        injectType: 'write',
        nodesToWrite,
        valuesToWrite
    };

    return [msg, forwardMsg, null];
} else {
    createError("Module is not in CALIBRATION mode");
    return [null, null, msg];
}

