/** @type {string} */
const workpieceId = msg.workpieceId;

msg.payload = createOrder("STORAGE", workpieceType, workpieceId);
msg.topic = ccuOrderTopic;
return msg;

/**
 * @typedef {object} Workpiece
 * @property {string} workpieceId
 * 
 * @typedef {object} Metadata
 * @property {string} [type]
 * @property {string} [workpieceId]
 * @property {Workpiece} [workpiece]
 * 
 * @typedef {object} Action
 * @property {string} id
 * @property {string} command
 * @property {string} [state]
 * @property {Metadata} metadata
 * 
 * @typedef {object} Order
 * @property {string} orderId
 * @property {number} orderUpdateId
 * @property {string} timestamp
 * @property {Action} action
 */

/**
 * @param {string} id - the id of the order
 * @param {string} command - the command to execute
 * @returns {Order}
 */
function createOrder(id, command) {
    return {
        timestamp: new Date().toISOString(),
        orderId: id,
        orderUpdateId: flow.get("orderUpdateId"),
        action: {
            id,
            command,
            metadata: {}
        }
    };
}

/** @type {string} */
const txtTopicBase = flow.get("TXT_MQTT_topic");
/** @type {string} */
const orderId = msg.payload.orderId;
flow.set("orderId", orderId);

msg.payload = createOrder(orderId, "NFC_NIO");
msg.topic = txtTopicBase + "/order";
return msg;

/** @type {string} */
const txtTopicBase = flow.get("TXT_MQTT_topic");
/** @type {string} */
const spsSerial = flow.get("serialNumber");
msg.topic = `${txtTopicBase}/instantAction`;
msg.payload.serialNumber = spsSerial;
return msg;

/**
 * @typedef {object} NodeInfo
 * @template T
 * @property {string} nodeId
 * @property {string} nodeName
 * @property {T} nodeValue
 */

/** @type {NodeInfo<number>} */
const posX = flow.get("calibration.state.befehl__bewegen_position_X");
/** @type {NodeInfo<number>} */
const posY = flow.get("calibration.state.befehl__bewegen_position_Y");
/** @type {NodeInfo<number>} */
const limitX = flow.get("calibration.state.kalib__grenze_x_oben");
/** @type {NodeInfo<number>} */
const limitY = flow.get("calibration.state.kalib__grenze_y_oben");
/** @type {NodeInfo<boolean>} */
const cmdHome = flow.get("calibration.state.befehl__HOME");
/** @type {NodeInfo<boolean>} */
const cmdMove = flow.get("calibration.state.befehl__BEWEGEN");
/** @type {NodeInfo<boolean>} */
const isMoving = flow.get("calibration.state.status__bewegen_aktiv");

let nodesToWrite = [];
let valuesToWrite = [];
const moveCmd = msg.payload.cmd;
msg.moveCmd = moveCmd;
/**
 * MoveDegree is sent as number (e.g. 20 -> 20) but needs to be converted into the encoder ticks
 * Estimated conversion factor horizontal: 1400 ticks ~ 160 -> 1 ~ 9 ticks
 * Estimated conversion factor vertical: 700 ticks ~ 130 -> 1 ~ 6 ticks 
 * 
 */
const moveDegree = msg.payload.degree ?? 0;
msg.moveDegree = moveDegree || -1;
const allowedCmds = ["home", "relmove_up", "relmove_down", "relmove_left", "relmove_right", "start_pan", "end_pan", "start_tilt", "end_tilt"];

node.warn({ isIdle: isMoving.nodeValue == false });
if (isMoving.nodeValue != false) {
    return;
}
if (allowedCmds.indexOf(moveCmd) == -1) {
    return;
}

switch (moveCmd) {
    case "home": {
        nodesToWrite.push({ nodeName: cmdHome.nodeName, nodeId: cmdHome.nodeId, datatypeName: "Boolean" });
        valuesToWrite.push({ value: true });
        break;
    }
    case "relmove_up": {
        const targetValue = Math.min(posY.nodeValue + moveDegree * 6, limitY.nodeValue);
        nodesToWrite.push({ nodeName: posY.nodeName, nodeId: posY.nodeId, datatypeName: "Int32" });
        valuesToWrite.push({ value: targetValue });
        nodesToWrite.push({ nodeName: cmdMove.nodeName, nodeId: cmdMove.nodeId, datatypeName: "Boolean" });
        valuesToWrite.push({ value: true });
        break;
    }
    case "relmove_down": {
        const targetValue = Math.max(posY.nodeValue - moveDegree * 6, 0);
        nodesToWrite.push({ nodeName: posY.nodeName, nodeId: posY.nodeId, datatypeName: "Int32" });
        valuesToWrite.push({ value: targetValue });
        nodesToWrite.push({ nodeName: cmdMove.nodeName, nodeId: cmdMove.nodeId, datatypeName: "Boolean" });
        valuesToWrite.push({ value: true });
        break;
    }
    case "relmove_left": {
        const targetValue = Math.max(posX.nodeValue - moveDegree * 9, 0);
        nodesToWrite.push({ nodeName: posX.nodeName, nodeId: posX.nodeId, datatypeName: "Int32" });
        valuesToWrite.push({ value: targetValue });
        nodesToWrite.push({ nodeName: cmdMove.nodeName, nodeId: cmdMove.nodeId, datatypeName: "Boolean" });
        valuesToWrite.push({ value: true });
        break;
    }
    case "relmove_right": {
        const targetValue = Math.min(posX.nodeValue + moveDegree * 9, limitX.nodeValue);
        nodesToWrite.push({ nodeName: posX.nodeName, nodeId: posX.nodeId, datatypeName: "Int32" });
        valuesToWrite.push({ value: targetValue });
        nodesToWrite.push({ nodeName: cmdMove.nodeName, nodeId: cmdMove.nodeId, datatypeName: "Boolean" });
        valuesToWrite.push({ value: true });
        break;
    }
    case "start_pan": {
        nodesToWrite.push({ nodeName: posX.nodeName, nodeId: posX.nodeId, datatypeName: "Int32" });
        valuesToWrite.push({ value: 0 });
        nodesToWrite.push({ nodeName: cmdMove.nodeName, nodeId: cmdMove.nodeId, datatypeName: "Boolean" });
        valuesToWrite.push({ value: true });
        break;
    }
    case "end_pan": {
        nodesToWrite.push({ nodeName: posX.nodeName, nodeId: posX.nodeId, datatypeName: "Int32" });
        valuesToWrite.push({ value: limitX.nodeValue });
        nodesToWrite.push({ nodeName: cmdMove.nodeName, nodeId: cmdMove.nodeId, datatypeName: "Boolean" });
        valuesToWrite.push({ value: true });
        break;
    }
    case "start_tilt": {
        nodesToWrite.push({ nodeName: posY.nodeName, nodeId: posY.nodeId, datatypeName: "Int32" });
        valuesToWrite.push({ value: 0 });
        nodesToWrite.push({ nodeName: cmdMove.nodeName, nodeId: cmdMove.nodeId, datatypeName: "Boolean" });
        valuesToWrite.push({ value: true });
        break;
    }
    case "end_tilt": {
        nodesToWrite.push({ nodeName: posY.nodeName, nodeId: posY.nodeId, datatypeName: "Int32" });
        valuesToWrite.push({ value: limitY.nodeValue });
        nodesToWrite.push({ nodeName: cmdMove.nodeName, nodeId: cmdMove.nodeId, datatypeName: "Boolean" });
        valuesToWrite.push({ value: true });
        break;
    }
}

msg.payload = {
    nodetype: 'inject',
    injectType: 'write',
    nodesToWrite,
    valuesToWrite
};
return msg;

/**
 * @typedef {object} ActionMetadata
 * @property {string} [position]
 * @property {boolean} [factory]
 * @property {{referenceKey: string, referenceValue: number}[]} [references]
 * 
 * @typedef {object} Action
 * @property {"startCalibration" 
 *    | "stopCalibration" 
 *    | "setCalibrationValues" 
 *    | "storeCalibrationValues" 
 *    | "selectCalibrationPosition" 
 *    | "testCalibrationPosition" 
 *    | "resetCalibration"} actionType
 * @property {string} actionId
 * @property {ActionMetadata} metadata
 * 
 * @typedef {object} CalibrationInstantAction
 * @property {string} serialNumber
 * @property {Date} timestamp
 * @property {Action[]} actions
 * 
 * @typedef {object} ReferenceData
 * @property {string} referenceKey
 * @property {number | string} referenceValue
 * 
 * @typedef {object} CalibrationData
 * @property {string} infoType
 * @property {string} infoLevel
 * @property {ReferenceData[]} infoReferences
 * 
 * @typedef {object} CalibrationValue
 * @property {string} nodeId
 * @property {string} nodeName
 * @property {string} nodeValue
 */

/**
 * @param {string} nodeName
 * @param {number} nodeValue
 * @returns {ReferenceData}
 */
function createReferenceData(nodeName, nodeValue) {
    return {
        referenceKey: nodeName,
        referenceValue: nodeValue,
    };
}

/**
 * @returns {ReferenceData[]}
 */
function createReferenceDataArray() {
    /** @type {Record<string, CalibrationValue>} */
    const calibrationState = flow.get("calibration.state") ?? {};
    /** @type {ReferenceData[]} */
    return Object.keys(calibrationState)
        .filter(key => key.indexOf("kalib__") > -1 && key.indexOf("grenze") == -1)
        .reduce((agg, key) => {
            const { nodeName, nodeValue } = calibrationState[key];
            const refData = createReferenceData(nodeName, nodeValue);
            return [
                ...agg,
                refData
            ];
        }, []);
}

/**
 * Expand existing information with dps calibration data 
 */
function expandCalibrationInfo(information) {
    return information.map(data => {
        if (data.infoType == "calibration_status") {
            return {
                ...data,
                infoReferences: data.infoReferences.map(ref => {
                    if (ref.referenceKey == "POSITIONS.AVAILABLE") {
                        return {
                            ...ref,
                            referenceValue: `${ref.referenceValue},CAMERA_HOME,CAMERA_HBW`
                        };
                    }
                    return ref;
                }),
            };
        }
        if (data.infoType == "calibration_data") {
            const dpsRefData = createReferenceDataArray();
            /** @type {ReferenceData[]} */
            const filtered = data.infoReferences.filter(
                (/** @type {ReferenceData} */ref) => !dpsRefData.some(dr => ref.referenceKey == dr.referenceKey)
            );
            return {
                ...data,
                infoReferences: [
                    ...filtered,
                    ...dpsRefData,
                ],
            }
        }
        return data;
    });
}

/**
 * @param information
 */
function createState(information) {
    /** @type {object} */
    const state = flow.get("state");
    const timestamp = new Date().toISOString();
    const headerId = flow.get("headerId");

    state.headerId = headerId;
    state.operatingMode = "TEACHIN";
    state.timestamp = timestamp;
    state.paused = true;
    state.information = information;
    state.orderId = flow.get("orderId");
    state.orderUpdateId = flow.get("orderUpdateId");
    const flowErrors = flow.get("errors") ?? [];
    state.errors = Array.isArray(flowErrors) ? flowErrors : [flowErrors];

    // headerId Update
    flow.set("headerId", headerId + 1);
    flow.set("state", state);
    return state;
}

const moduleState = flow.get("moduleState");
if (moduleState == "CALIBRATION") {
    const updatedInformation = expandCalibrationInfo(msg.payload.information);
    const state = createState(updatedInformation);
    /** @type {string} */
    const spsTopic = flow.get("MQTT_topic");
    msg.topic = spsTopic + "/state";
    msg.payload = state;

    return msg;
}
return null;

node.status({ fill: "gray", shape: "ring", text: `Input: ${msg.nodeValue}` });
flow.set("workpieceInput", msg.nodeValue);
if (msg.nodeValue == false) {
    // Skip processing for empty input
    // and stop resending messages
    return [null, { resend_ignore: true }];
}

const spsSerial = flow.get("state.serialNumber") ?? null;
if (spsSerial == "notReadYet" || spsSerial == null) {
    return [null, null];
}

if (flow.get("moduleState") !== "IDLE") {
    // Start resending messages while BUSY
    return [null, null];
};

flow.set("moduleState", "WP_INPUT");

// Stop resending messages after successful processing
return [msg, { resend_ignore: true }];

const moduleState = flow.get("moduleState");
if (moduleState == "WP_INPUT") {
    flow.set("moduleState", "WP_INPUT_SENT")
    return msg;
} else {
    // do not publish when input is not active.
    return null;
}

const red = msg.payload.actions[0].metadata.red;
const yellow = msg.payload.actions[0].metadata.yellow;
const green = msg.payload.actions[0].metadata.green;

msg.payload = {
    nodetype: 'inject',
    injectType: 'write',
    nodesToWrite: [
        { nodeId: 'ns=4;i=19'/* red */, "datatypeName": "Boolean" },
        { nodeId: 'ns=4;i=20'/* green */, "datatypeName": "Boolean" }, // in OPCUA ist die bennung aktuell vertauscht worden (20231011)
        { nodeId: 'ns=4;i=21'/* yellow */, "datatypeName": "Boolean" } // in OPCUA ist die bennung aktuell vertauscht worden (20231011)
    ],
    valuesToWrite: [
        { value: red },
        { value: green },
        { value: yellow }
    ]
};
return msg;

const moduleState = flow.get("moduleState");
if (moduleState == "WP_INPUT_SENT") {
    // let robot abort
    flow.set("moduleState", "WP_INPUT_ABORTED");
    return [msg, null];
} else if (moduleState != "IDLE") {
    return [null, msg];
}

// Timestamp
let now = new Date();
let timestamp = now.toISOString();

// state bauen
let state = flow.get("state");
state.headerId = flow.get("headerId");
state.loads = flow.get("loads");
state.actionState = flow.get("actionState");
state.actionState.state = "FAILED";
state.timestamp = timestamp;
state.orderId = flow.get("orderId");
state.orderUpdateId = flow.get("orderUpdateId");
const flowErrors = flow.get("errors") ?? [];
state.errors = Array.isArray(flowErrors) ? flowErrors : [flowErrors];

// Flow Update
flow.set("errors", []);
flow.set("headerId", state.headerId + 1);

msg.topic = flow.get("MQTT_topic") + "/state";
msg.payload = state
return msg;
